import {Injectable} from '@angular/core';
import {HttpService} from "./http.service";

declare let  Wechat: any;

@Injectable()
export class WXService {

     isInstalled = true;

    constructor(public http: HttpService) {


    }

    getWXIsInstalled(){


            Wechat.isInstalled(installed => {

                if(installed){
                    this.isInstalled = true;
                } else {
                    this.isInstalled = false;
                }

            },error => {

                this.isInstalled = false;

            });


    }

    share(content,imgUrl?){


        imgUrl = this.getImageUrl();
        return new Promise( (resolve, reject) => {

              Wechat.share({

                  message: {
                      title: content,
                      description: "tz",
                      thumb: imgUrl || "",
                      media: {
                          type: Wechat.Type.WEBPAGE,   // webpage
                          webpageUrl: "http://cswapp.hichengdai.com"   // webpage
                      }
                  },
                  scene: Wechat.Scene.TIMELINE   // share to Timeline

              }, function () {

                  resolve();


              }, function (reason) {

                  reject("shareFailure");

              });

        });

    }

    wxGetAuthCode(){
        return new Promise((resolve,reject) => {

            let scope = "snsapi_userinfo";
            let state = "csw_wx_login";

            Wechat.auth(scope, state, response => {

                resolve(response["code"]);

            }, reason => {

                reject();

            });


        });

    }

    wxLogin(wxWebCode?: string){

        return new Promise((resolve,reject) => {

            let scope = "snsapi_userinfo";
            // let state = "_" + (+new Date());
            let state = "csw_wx_login";


            Wechat.auth(scope, state, response => {

                // //2.通过code获取accseeToken
                let getTokenObj;

                console.log("微信登陆" + response["code"])
                getTokenObj = {
                    code: response["code"],
                    appid: "wxa6c6d2544f85d4b9",
                    secret: "eb0daccd6e456f604fc5dde0d14d6c69"
                    // grant_type : "authorization_code"
                }

                    // ,"/auth2/login/wx"
                //获取userID tokenId  增加参数 companycode 和手机号码
                this.http.get("/auth2/login/wx",getTokenObj).then(res => {

                     resolve(res);

                }).catch(error => {

                    reject();

                });



            }, reason => {

                reject();

            });


        });

    }

     getImageUrl(){

        return "data:image/png;base64,";

    }

}